        ).then(data =>{
          console.log(data)
        }

        )
      });

      const handleSubmit = (e) => {
        e.preventDefault()
        console.log(username)
        console.log(password)
        APIService.InsertData({username,password})

        setUsername('')
        setPassword('')


      }

    return (
      <div>

        <form className="form" onSubmit={handleSubmit}>

            <label htmlFor="username">UserName</label>
